assert.match(code, /if\s*\(\s*a\.title\s*>\s*b\.title\s*\)\s*{\s*return\s*1\s*;?\s*}|if\s*\(\s*a\.title\s*>\s*b\.title\s*\)\s*return\s*1\s*;?/);
